/// <reference types="vite/client" />

/**
 * Type declarations for GLSL file imports
 */

declare module '*.glsl' {
  const content: string;
  export default content;
}

declare module '*.vert' {
  const content: string;
  export default content;
}

declare module '*.frag' {
  const content: string;
  export default content;
}
